import{c as t,a as o,o as e}from"./index-BgAtpXmQ.js";const a={class:"markdown-body"},n="default",p="Алхимические материалы",m=!0,d={__name:"alchemical-materials",setup(r,{expose:i}){return i({frontmatter:{layout:"default",title:"Алхимические материалы",protect:!0}}),(s,l)=>(e(),t("div",a,l[0]||(l[0]=[o('<h4>Варианты для защиты от магического старения:</h4><p><strong>Укрепляющие экстракты для защиты магических каналов:</strong></p><ol><li>Экстракт Кристаллического Папоротника (Pteridium Crystallum)</li></ol><ul><li>Растение с жесткими волокнами, насыщенными минеральными структурами</li><li>Усиливает магическую резонансность и препятствует деградации каналов</li><li>Особенно эффективен для древесины с нестабильной магической структурой</li></ul><ol start="2"><li>Настой Стальной Лозы (Vinca Ferrum)</li></ol><ul><li>Минеральная лиана с высоким содержанием железных включений</li><li>Создает плотный защитный слой, блокирующий агрессивные магические поля</li><li>Подходит для длительной консервации магических артефактов</li></ul><ol start="3"><li>Экстракт Сердцевины Горного Хрусталя (Quartz Medulla)</li></ol><ul><li>Натуральный минеральный концентрат с высокой энергетической стабильностью</li><li>Выравнивает внутреннюю структуру магических каналов</li><li>Обеспечивает равномерную защиту по всей поверхности</li></ul><ol start="4"><li>Сок Магнитного Мха (Muscus Magneticus)</li></ol><ul><li>Редкий мох, растущий на участках с особыми геомагнитными характеристиками</li><li>Создает гибкий, но прочный защитный слой</li><li>Способен самовосстанавливаться при незначительных повреждениях</li></ul><ol start="5"><li>Концентрат Оберегающего Корня (Radix Protego)</li></ol><ul><li>Корневая система с высоким содержанием защитных минералов</li><li>Формирует многослойную магическую броню</li><li>Подходит для особо чувствительных магических материалов</li></ul><p>Каждый из этих экстрактов имеет уникальные свойства и может быть выбран в зависимости от:</p><ul><li>Типа магической древесины</li><li>Интенсивности внешних магических воздействий</li><li>Длительности требуемой защиты</li><li>Доступности материала</li></ul><p>Рекомендация: перед применением провести пробное тестирование совместимости экстракта с конкретным типом древесины.</p>',15)])))}};export{d as default,n as layout,m as protect,p as title};
